package ru.ifmo.ctddev.antonov.implementor;

/**
 * Class that contains constants for {@link Implementor}, {@link ImplConstructor}, {@link ImplMethod}.
 */
public class Constants {
    /**
     * Name of arguments in every generated method and constructor
     */
    public static final String NAME_OF_ARG = "arg";
    /**
     * Name of suffix that will be added to implemented class
     */
    public static final String SUFFIX = "Impl";
    /**
     * Name of charset that is used for generated files
     */
    public static final String CHARSET = "UTF-8";
}
